import Swiper from 'https://cdn.jsdelivr.net/npm/swiper@11/swiper-bundle.mjs'
document.addEventListener('DOMContentLoaded',function(){initSwiper()
showPassword()
function initSwiper(){const formSlider=new Swiper('.form-slider',{slidesPerView:1,effect:'fade',fadeEffect:{crossFade:!0},autoHeight:!0,pagination:{el:'.swiper-pagination',clickable:!0}})
const carouselSlider=new Swiper('.carousel-slider',{loop:!0,autoplay:{delay:3000,disableOnInteraction:!1},slidesPerView:'auto',speed:800,spaceBetween:20,centeredSlides:!0})
const communitySlider=new Swiper('.community-slider',{loop:!0,autoplay:{delay:3000,disableOnInteraction:!1},slidesPerView:'auto',speed:800,spaceBetween:20,centeredSlides:!0})
const whySlider=new Swiper('.why-slider',{loop:!0,autoplay:{delay:4000,disableOnInteraction:!1},slidesPerView:'auto',speed:800,spaceBetween:20,centeredSlides:!0,init:!1})
function handleSwiper(){if(window.innerWidth<=991.98){if(!whySlider.initialized){whySlider.init()}}else{if(whySlider.initialized){whySlider.destroy(!0,!0)}}}
handleSwiper()}
function showPassword(){const passwordInput=document.getElementById('password')
const togglePassword=document.querySelector('.toggle-password')
togglePassword.addEventListener('click',function(){if(passwordInput.type==='password'){passwordInput.type='text'}else{passwordInput.type='password'}})}})